import React from 'react';
import { useNavigate } from 'react-router-dom';
import './events.css';
// Event data
const events = [
  { "id": 1, "name": "Yoga Retreat", "date": "2024-12-15", "image": "yoga.jpg" },
  { "id": 2, "name": "Coding Hackathon", "date": "2024-12-20", "image": "hackathon.jpg" },
  { "id": 3, "name": "Stand-up Comedy Night", "date": "2024-12-23", "image": "comedy.jpg" },
  { "id": 4, "name": "Book Fair", "date": "2024-12-28", "image": "bookfair.jpg" },
  { "id": 5, "name": "Charity Run", "date": "2025-01-05", "image": "charityrun.jpg" },
];

function Events() {
  const navigate = useNavigate(); // Hook for programmatic navigation

  const handleViewDetails = (id) => {
    navigate(); // Navigates to the event details page with the event ID
  };

  return (
    <div className="events-container">
    <h1 className="page-heading">EVENTS</h1> {/* Added Page Heading */}
<div className="events-list">
  {events.map((event) => (
    <div key={event.id} className="event-item">
      <img src={event.image} alt={event.name} className="event-image" />
      <h3>{event.name}</h3>
      <p>{event.date}</p>
      <button
        className="view-details-btn"
        onClick={() => handleViewDetails(event.id)}
      >
        View Details
      </button>
    </div>
  ))}
</div>
</div>
);
}

export default Events
