import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useLocation } from "react-router-dom";
import "./addevent.css";

const AddEvent = () => {
  const navigate = useNavigate();
  const location = useLocation(); // Access state passed from the event list
  const [isEditMode, setIsEditMode] = useState(false); // Track mode (Add or Edit)

  // Initialize event data with defaults
  const [eventData, setEventData] = useState({
    event_name: "",
    country: "",
    state: "",
    city: "",
    address: "",
    category: "",
    dateandtime: "",
    organised_by: "",
    description_event: "",
  });
  const [image, setImage] = useState(null); // For image file
  const [eventId, setEventId] = useState(null); // Store event ID for editing
  useEffect(() => {
    document.body.classList.add("add-event-background");

    return () => {
      document.body.classList.remove("add-event-background");
    };
  }, []);


  useEffect(() => {
    if (location.state?.event) {
      setIsEditMode(true);
      const event = location.state.event;
      setEventData((prevData) => ({
        ...prevData, // Retain default values for missing fields
        ...event,    // Overwrite with existing event data
      }));
      setEventId(event.event_id);
    }
  }, [location.state]);

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Log eventData for debugging
    console.log("Event data before submission:", eventData);
  
    // Create FormData for POST/PUT requests
    const formData = new FormData();
    formData.append("event_name", eventData.event_name);
    formData.append("country", eventData.country);
    formData.append("state", eventData.state);
    formData.append("city", eventData.city);
    formData.append("address", eventData.address);
    formData.append("category", eventData.category);
    formData.append("dateandtime", eventData.dateandtime);
    formData.append("organised_by", eventData.organised_by);
    formData.append("description_event", eventData.description_event);
    
    // Append image only if a new file is selected
    if (image) {
      formData.append("image_url", image);
    }
  
    try {
      if (isEditMode) {
        // Log the eventId for debugging
        console.log("Editing event with ID:", eventId);
  
        // Correct template string
        await axios.put(`http://127.0.0.1:8000/events/${eventId}`, formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        });
        alert("Event updated successfully!");
      } else {
        await axios.post("http://127.0.0.1:8000/events", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        });
        alert("Event added successfully!");
      }
      navigate("/getevents");
    } catch (err) {
      console.error("Error details:", err.response?.data || err.message);
      alert("Failed to submit the form. Please try again.");
    }
  };
     return (
    <div className="add-event-form">
      <h1>{isEditMode ? "Edit Event" : "Add New Event"}</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="event_name"
          placeholder="Event Name"
          value={eventData.event_name}
          onChange={handleChange}
          required
        />
        <input
          type="file"
          name="image_url"
          onChange={handleImageChange}
        />
        <input
          type="datetime-local"
          name="dateandtime"
          placeholder="Date and Time"
          value={eventData.dateandtime}
          onChange={handleChange}
          required
        />
        <select
          name="category"
          value={eventData.category}
          onChange={handleChange}
          required
        >
          <option value="" disabled>
            Select Category
          </option>
          <option value="Health & Wellness">Health & Wellness</option>
          <option value="Technology">Technology</option>
          <option value="Entertainment">Entertainment</option>
          <option value="Education">Education</option>
          <option value="Sports">Sports</option>
          <option value="Marriage">Marriage</option>
          <option value="Other">Other</option>
        </select>
        <input
          type="text"
          name="organised_by"
          placeholder="Organised By"
          value={eventData.organised_by}
          onChange={handleChange}
          required
        />
        <textarea
          name="description_event"
          className="description-input"
          placeholder="Description"
          value={eventData.description_event}
          onChange={handleChange}
          rows="4"
        />
        <input
          type="text"
          name="country"
          placeholder="Country"
          value={eventData.country}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="state"
          placeholder="State"
          value={eventData.state}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="city"
          placeholder="City"
          value={eventData.city}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={eventData.address}
          onChange={handleChange}
          required
        />
        <button type="submit" className="submit-btn">
          {isEditMode ? "Update Event" : "Submit"}
        </button>
      </form>
    </div>
  );
};

export default AddEvent;
