import React, { useState } from "react";
import EventList from "./eventlist";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
  } from "chart.js";
  
  
  ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);
  
import axios from "axios";
import "./dashboard.css";

const DashboardAndEvents = () => {
  const [activeTab, setActiveTab] = useState("Dashboard");
  const [events, setEvents] = useState([]);
  const [eventsThisWeek, setEventsThisWeek] = useState(0);
  const [tomorrowEvents, setTomorrowEvents] = useState([]);

  // Fetch events data for Dashboard
  React.useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/events");
        const allEvents = response.data;
        setEvents(allEvents);

        // Calculate events this week
        const today = new Date();
        const startOfWeek = new Date(today.setDate(today.getDate() - today.getDay() + 1)); // Monday
        const endOfWeek = new Date(today.setDate(startOfWeek.getDate() + 6)); // Sunday

        const thisWeekEvents = allEvents.filter((event) => {
          const eventDate = new Date(event.dateandtime);
          return eventDate >= startOfWeek && eventDate <= endOfWeek;
        });

        setEventsThisWeek(thisWeekEvents.length);

        // Get tomorrow's events
        const tomorrow = new Date();
        tomorrow.setDate(tomorrow.getDate() + 1);
        const tomorrowEvents = allEvents.filter((event) => {
          const eventDate = new Date(event.dateandtime);
          return eventDate.toDateString() === tomorrow.toDateString();
        });

        setTomorrowEvents(tomorrowEvents);
      } catch (error) {
        console.error("Error fetching events:", error);
      }
    };

    fetchEvents();
  }, []);
  useEffect(() => {
    const chartInstance = new Chart(ctx, chartConfig);
  
    return () => {
      chartInstance.destroy();
    };
  }, [dependencies]);
  

  // Chart data
  const chartData = {
    labels: ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"],
    datasets: [
      {
        label: "Events This Week",
        data: Array(7).fill(0).map((_, index) => {
          const dayStart = new Date();
          dayStart.setDate(dayStart.getDate() - dayStart.getDay() + index + 1);
          const dayEnd = new Date(dayStart);
          dayEnd.setHours(23, 59, 59, 999);

          return events.filter((event) => {
            const eventDate = new Date(event.dateandtime);
            return eventDate >= dayStart && eventDate <= dayEnd;
          }).length;
        }),
        backgroundColor: "#4CAF50",
      },
    ],
  };

  return (
    <div className="tabs-container">
      <header className="tabs-header">
        <button className={activeTab === "Dashboard" ? "active" : ""} onClick={() => setActiveTab("Dashboard")}>
          Dashboard
        </button>
        <button className={activeTab === "Events" ? "active" : ""} onClick={() => setActiveTab("Events")}>
          Events
        </button>
      </header>

      <div className="tab-content">
        {activeTab === "Dashboard" && (
          <div className="dashboard">
            <h2>Dashboard</h2>
            <p><strong>Number of Events This Week: {eventsThisWeek}</strong></p>
            <div className="chart-container">
              <Bar data={chartData} />
            </div>
            <div className="tomorrow-notifications">
              <h3>Tomorrow's Events</h3>
              {tomorrowEvents.length > 0 ? (
                <ul>
                  {tomorrowEvents.map((event) => (
                    <li key={event.event_id}>
                      {event.event_name} at {new Date(event.dateandtime).toLocaleTimeString()}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No events scheduled for tomorrow.</p>
              )}
            </div>
          </div>
        )}

        {activeTab === "Events" && <EventList />}
      </div>
    </div>
  );
};

export default DashboardAndEvents;
