import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "./eventlist.css";
const EventList = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const location = useLocation(); 
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch events from the backend
    const fetchEvents = async () => {
      try {
        if (location.state?.events) {
          // Use events passed from AddEvent if available
          setEvents(location.state.events);
        } else {
          const response = await axios.get("http://127.0.0.1:8000/events");
          setEvents(response.data);
        }
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch events. Please try again.");
        setLoading(false);
      }
    };

    fetchEvents();
  }, [location.state]); // Re-fetch events if location.state changes

  if (loading) return <p>Loading events...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  const handleEdit = (eventId) => {
    navigate(`/edit-event/${eventId}`);
  };

  return (
    <div className="event-list">
      {/* Header with Navbar */}
      <header className="navbar">
        <h1 className="navbar-title">Event Management</h1>
        <div className="navbar-links">
          <button className="navbar-btn" onClick={() => navigate("/add-event")}>
            Add Event
          </button>
          <button className="navbar-btn" onClick={() => navigate("/login")}>
            Log out
          </button>
        </div>
      </header>

      <div className="events-container">
        {events.map((event) => (
          <div key={event.event_id} className="event-card">
            <img src={event.image_url} alt={event.event_name} className="event-image" />
            <h2>{event.event_name}</h2>
            <p><strong>Date:</strong> {new Date(event.dateandtime).toLocaleString()}</p>
            <p><strong>Category:</strong> {event.category}</p>
            <p><strong>Organised By:</strong> {event.organised_by}</p>
            <p>{event.description_event}</p>
            <p><strong>Location:</strong> {`${event.city}, ${event.state}, ${event.country}`}</p>
            <p><strong>Address:</strong> {event.address}</p>
            <button className="edit-btn" onClick={() => handleEdit(event.event_id)}>
              Edit
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventList;
