import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "./eventlist.css";

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [role, setUserRole] = useState("User"); 
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const storedRole = sessionStorage.getItem("role");
    console.log("Retrieved role from sessionStorage:", storedRole); 
    setUserRole(storedRole || "User"); 

    const fetchEvents = async () => {
      try {
        if (location.state?.events) {
          setEvents(location.state.events);
        } else {
          const response = await axios.get("http://127.0.0.1:8000/events");
          setEvents(response.data);
        }
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch events. Please try again.");
        setLoading(false);
      }
    };

    fetchEvents();
  }, [location.state]);

  const handleDelete = async (eventId) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      try {
        await axios.delete(`http://127.0.0.1:8000/events/${eventId}`);
        alert("Event deleted successfully!");
        setEvents(events.filter((event) => event.event_id !== eventId));
      } catch (err) {
        console.error("Error deleting event:", err.response?.data || err.message);
        alert("Failed to delete the event. Please try again.");
      }
    }
  };

  if (loading) return <p className="loading">Loading events...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="event-list">
      <div className="top">
        <img
          src="https://www.logolynx.com/images/logolynx/7f/7f0b42ea89979e09c58b53a13e4565d9.jpeg"
          alt="Logo"
          className="header-logo"
        />
        <div className="navbar-title">Event Management</div>
        <header className="navbar">
          {role === "admin" && (
            <button className="navbar-btn" onClick={() => navigate("/add-event")}>Add Event</button>
          )}
          <button className="navbar-btn" onClick={() => navigate("/")}>Log out</button>
        </header>
      </div>
      <div className="events-container">
        <table className="event-table">
          <thead>
            <tr>
              <th>Event Name</th>
              <th>Date</th>
              <th>Category</th>
              <th>Organised By</th>
              <th>Location</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event) => (
              <tr key={event.event_id}>
                <td>{event.event_name}</td>
                <td>{new Date(event.dateandtime).toLocaleString()}</td>
                <td>{event.category}</td>
                <td>{event.organised_by}</td>
                <td>{`${event.city}, ${event.state}, ${event.country}`}</td>
                <td>
                  {role === "admin" && (
                    <>
                      <button
                        className="edit-btn"
                        onClick={() => navigate("/add-event", { state: { event } })}
                      >
                        Edit
                      </button>
                      <button
                        className="delete-btn"
                        onClick={() => handleDelete(event.event_id)}
                      >
                        Delete
                      </button>
                    </>
                  )}
                  <button
                    className="view-btn"
                    onClick={() => navigate("/event-details", { state: { event } })}
                  >
                    View Details
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EventList;
