import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './eventdetails.css'; 

// Event data
const events = [
  { "id": 1, "name": "Yoga Retreat", "date": "2024-12-15", "image": "yoga.jpg",place: 'Stadium', time: '6:00 PM'  },
  { "id": 2, "name": "Coding Hackathon", "date": "2024-12-20", "image": "hackathon.png",place: 'Gallery', time: '10:00 AM' },
  { "id": 3, "name": "Stand-up Comedy Night", "date": "2024-12-23", "image": "comedy.png" ,place: 'Stadium', time: '6:00 PM' },
  { "id": 4, "name": "Book Fair", "date": "2024-12-28", "image": "bookfair.png",place: 'City Park', time: '4:00 PM'  },
  { "id": 5, "name": "Marriage", "date": "2025-01-05", "image": "marriage.png" ,place: 'Convention Center', time: '10:00 AM'},
];

function EventDetails() {
  const navigate = useNavigate(); // For navigation
  const { id } = useParams(); // Get event ID from URL
  const event = events.find((e) => e.id === parseInt(id));

  if (!event) {
    return <div>Event not found</div>;
  }

  return (
    <div
      className="event-details"

    >
      <div className="event-details-content">
        <h2>{event.name}</h2>
        <p>Date: {event.date}</p>
        <p>Place: {event.place}</p>
        <p>Time: {event.time}</p>
        <button className="back-btn" onClick={() => navigate('/events')}>
          Back to Events
        </button>
      </div>
    </div>
  );
}

