import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "./eventlist.css";

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [userRole, setUserRole] = useState(""); // Role of logged-in user
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the role of the user from local storage or backend
    const role = localStorage.getItem("userRole"); // Assume role is stored in localStorage
    setUserRole(role);

    const fetchEvents = async () => {
      try {
        if (location.state?.events) {
          setEvents(location.state.events);
        } else {
          const response = await axios.get("http://127.0.0.1:8000/events");
          setEvents(response.data);
        }
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch events. Please try again.");
        setLoading(false);
      }
    };

    fetchEvents();
  }, [location.state]);

  const handleDelete = async (eventId) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      try {
        await axios.delete(`http://127.0.0.1:8000/events/${eventId}`);
        alert("Event deleted successfully!");
        setEvents(events.filter((event) => event.event_id !== eventId));
      } catch (err) {
        console.error("Error deleting event:", err.response?.data || err.message);
        alert("Failed to delete the event. Please try again.");
      }
    }
  };

  if (loading) return <p>Loading events...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="event-list">
      <div className="top">
        <div className="header">
          <img
            src="https://www.logolynx.com/images/logolynx/7f/7f0b42ea89979e09c58b53a13e4565d9.jpeg"
            alt="Logo"
            className="header-logo"
          />
        </div>
        <header className="navbar">
          <div className="navbar-section left">
            <button className="navbar-btn" onClick={() => navigate("/login")}>
              Log out
            </button>
          </div>
          <div className="navbar-title">Event Management</div>
          <div className="navbar-section right">
            <img
              src="https://via.placeholder.com/40" // Replace with actual profile picture URL if available
              alt="Profile"
              className="profile-logo"
            />
            <span className="user-role">{userRole}</span>
            {userRole === "admin" && (
              <button className="navbar-btn" onClick={() => navigate("/add-event")}>
                Add Event
              </button>
            )}
          </div>
        </header>
      </div>
      <div className="events-container">
        {events.map((event) => (
          <div key={event.event_id} className="event-card">
            <img
              src={event.image_url}
              alt={event.event_name}
              className="event-image"
            />
            <h2>{event.event_name}</h2>
            <p>
              <strong>Date:</strong> {new Date(event.dateandtime).toLocaleString()}
            </p>
            <p>
              <strong>Category:</strong> {event.category}
            </p>
            <p>
              <strong>Organised By:</strong> {event.organised_by}
            </p>
            <p>
              <strong>Location:</strong> {`${event.city}, ${event.state}, ${event.country}`}
            </p>
            <p>
              <strong>Address:</strong> {event.address}
            </p>
            <div className="card-buttons">
              <button
                className="view-btn"
                onClick={() => navigate("/event-details", { state: { event } })}
              >
                View Details
              </button>
              {userRole === "admin" && (
                <>
                  <button
                    className="edit-btn"
                    onClick={() => navigate("/add-event", { state: { event } })}
                  >
                    Edit
                  </button>
                  <button
                    className="delete-btn"
                    onClick={() => handleDelete(event.event_id)}
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventList;
