import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useLocation } from "react-router-dom";
import "./addevent.css";

const AddEvent = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isEditMode, setIsEditMode] = useState(false);
  const [eventData, setEventData] = useState({
    event_name: "",
    country: "",
    state: "",
    city: "",
    address: "",
    category: "",
    dateandtime: "",
    organised_by: "",
    description: "", // Ensure description field is included
  });
  const [image, setImage] = useState(null);
  const [eventId, setEventId] = useState(null);

  const event = location.state?.event;

  useEffect(() => {
    document.body.classList.add("add-event-background");

    return () => {
      document.body.classList.remove("add-event-background");
    };
  }, []);

  useEffect(() => {
    if (event) {
      console.log("Event data passed for editing:", event); // Debugging line
      setIsEditMode(true);
      setEventData((prevData) => ({
        ...prevData,
        ...event,
      }));
      setEventId(event.event_id);
    }
  }, [event]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEventData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.entries(eventData).forEach(([key, value]) => {
      formData.append(key, value);
    });
    if (image) formData.append("image_url", image);

    try {
      if (isEditMode) {
        await axios.put(`http://127.0.0.1:8000/events/${eventId}`, formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        alert("Event updated successfully!");
      } else {
        await axios.post("http://127.0.0.1:8000/events", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        alert("Event added successfully!");
      }
      navigate("/getevents");
    } catch (err) {
      console.error("Error details:", err.response?.data || err.message);
      alert("Failed to submit the form. Please try again.");
    }
  };

  return (
    <div className="add-event-form">
      <h1>{isEditMode ? "Edit Event" : "Add New Event"}</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="event_name"
          placeholder="Event Name"
          value={eventData.event_name}
          onChange={handleChange}
          required
        />
        <input type="file" name="image_url" onChange={handleImageChange} />
        <input
          type="datetime-local"
          name="dateandtime"
          placeholder="Date and Time"
          value={eventData.dateandtime}
          onChange={handleChange}
          required
        />
        <select
          name="category"
          value={eventData.category}
          onChange={handleChange}
          required
        >
          <option value="" disabled>
            Select Category
          </option>
          <option value="Health & Wellness">Health & Wellness</option>
          <option value="Technology">Technology</option>
          <option value="Entertainment">Entertainment</option>
          <option value="Education">Education</option>
          <option value="Sports">Sports</option>
          <option value="Marriage">Marriage</option>
          <option value="Other">Other</option>
        </select>
        <input
          type="text"
          name="organised_by"
          placeholder="Organised By"
          value={eventData.organised_by}
          onChange={handleChange}
          required
        />
        <textarea
          name="description"
          className="description-input"
          placeholder="Description"
          value={eventData.description}
          onChange={handleChange}
          rows="4"
        />
        <input
          type="text"
          name="country"
          placeholder="Country"
          value={eventData.country}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="state"
          placeholder="State"
          value={eventData.state}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="city"
          placeholder="City"
          value={eventData.city}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={eventData.address}
          onChange={handleChange}
          required
        />
        <button type="submit" className="submit-btn">
          {isEditMode ? "Update Event" : "Submit"}
        </button>
      </form>
    </div>
  );
};

export default AddEvent;
