import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "./eventlist.css";

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const location = useLocation(); // Access state passed from AddEvent
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch events from the backend
    const fetchEvents = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/events");
        let fetchedEvents = response.data;

        // Add the new event if passed from AddEvent
        if (location.state?.newEvent) {
          fetchedEvents = [...fetchedEvents, location.state.newEvent];
        }

        setEvents(fetchedEvents);
        setLoading(false); 
      } catch (err) {
        setError("Failed to fetch events. Please try again.");
        setLoading(false);
      }
    };

    fetchEvents();
  }, [location.state]); // Re-run if location.state changes

  if (loading) return <p>Loading events...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="event-list">
      <div className="header">
        <h1>Upcoming Events</h1>
        <button
          className="add-event-btn"
          onClick={() => navigate("/add-event")}
        >
          Add New Event
        </button>
      </div>
      <div className="events-container">
        {events.map((event) => (
          <div key={event.event_id || event.event_name} className="event-card">
            <img
              src={event.image_url}
              alt={event.event_name}
              className="event-image"
            />
            <h2>{event.event_name}</h2>
            <p>
              <strong>Date:</strong>{" "}
              {new Date(event.dateandtime).toLocaleString()}
            </p>
            <p>
              <strong>Location:</strong>{" "}
              {`${event.city}, ${event.state}, ${event.country}`}
            </p>
            <p>
              <strong>Category:</strong> {event.category}
            </p>
            <p>
              <strong>Address:</strong> {event.address}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventList;
