import React from 'react';
import { useNavigate } from 'react-router-dom';
import './events.css';
// Event data
const events = [
  { id: 1, name: 'Music Fest', date: '2024-11-25', image: "music.jpg" },
  { id: 2, name: 'Art Exhibition', date: '2024-12-01', image: "art.jpg" },
  { id: 3, name: 'Tech Conference', date: '2024-12-05', image: "tech.jpg" },
  { id: 4, name: 'Food Carnival', date: '2024-12-10', image: "food.jpg" },
];

function Events() {
  const navigate = useNavigate(); // Hook for programmatic navigation

  const handleViewDetails = (id) => {
    navigate(); // Navigates to the event details page with the event ID
  };

  return (
    <div className="events-container">
    <h1 className="page-heading">EVENTS</h1> {/* Added Page Heading */}
<div className="events-list">
  {events.map((event) => (
    <div key={event.id} className="event-item">
      <img src={event.image} alt={event.name} className="event-image" />
      <h3>{event.name}</h3>
      <p>{event.date}</p>
      <button
        className="view-details-btn"
        onClick={() => handleViewDetails(event.id)}
      >
        View Details
      </button>
    </div>
  ))}
</div>
</div>
);
}

export default Events
