import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useLocation } from "react-router-dom";
import "./addevent.css"; // CSS for styling

const AddEvent = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isEditMode, setIsEditMode] = useState(false); // Check if editing
  const [eventId, setEventId] = useState(null); // Store event ID for editing

  const [eventData, setEventData] = useState({
    event_name: "",
    country: "",
    state: "",
    city: "",
    address: "",
    category: "",
    dateandtime: "",
    image_url: "",
    organised_by: "",
    description: "",
  });

  // Load event data if editing
  useEffect(() => {
    if (location.state?.event) {
      setIsEditMode(true);
      setEventId(location.state.event.event_id);
      setEventData(location.state.event); // Pre-fill form with event data
    }
  }, [location.state]);

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (isEditMode) {
        // Send PUT request for editing
        const formData = new FormData();
        for (const key in eventData) {
          formData.append(key, eventData[key]);
        }
        await axios.put(`http://127.0.0.1:8000/events/${eventId}`, formData);
        alert("Event updated successfully!");
      } else {
        // Send POST request for adding
        const formData = new FormData();
        for (const key in eventData) {
          formData.append(key, eventData[key]);
        }
        await axios.post("http://127.0.0.1:8000/events", formData);
        alert("Event added successfully!");
      }
      navigate("/getevents");
    } catch (err) {
      alert("Failed to submit the form. Please try again.");
    }
  };

  return (
    <div className="add-event-form">
      <h1>Add New Event</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="event_name"
          placeholder="Event Name"
          value={eventData.event_name}
          onChange={handleChange}
          required
        />
        <input
          type="file"
          name="image_url"
          onChange={handleImageChange}
          required
        />
        <input
          type="datetime-local"
          name="dateandtime"
          placeholder="Date and Time"
          value={eventData.dateandtime}
          onChange={handleChange}
          required
        />
        <select
          name="category"
          value={eventData.category}
          onChange={handleChange}
          required
        >
          <option value="" disabled>
            Select Category
          </option>
          <option value="Health & Wellness">Health & Wellness</option>
          <option value="Technology">Technology</option>
          <option value="Entertainment">Entertainment</option>
          <option value="Education">Education</option>
          <option value="Sports">Sports</option>
          <option value="Marriage">Marriage</option>
          <option value="Other">Other</option>
        </select>
        <input
          type="text"
          name="organised_by"
          placeholder="Organised By"
          value={eventData.organised_by}
          onChange={handleChange}
          required
        />
        <textarea
          name="description"
          className="description-input"
          placeholder="Description"
          value={eventData.description}
          onChange={handleChange}
          required
          rows="4"
        />
        <input
          type="text"
          name="country"
          placeholder="Country"
          value={eventData.country}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="state"
          placeholder="State"
          value={eventData.state}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="city"
          placeholder="City"
          value={eventData.city}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={eventData.address}
          onChange={handleChange}
          required
        />
        <button type="submit" className="submit-btn">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddEvent;
