import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./addevent.css"; 

const AddEvent = () => {
  const [eventData, setEventData] = useState({
    event_name: "",
    country: "",
    state: "",
    city: "",
    address: "",
    category: "",
    dateandtime: "",
    image_url: "", 
    organised_by: "", // Added organised_by
    description: "", // Added description
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://127.0.0.1:8000/events", eventData);
      alert("Event added successfully!");
      navigate("/"); // Redirect back to the event list
    } catch (err) {
      alert("Failed to add event. Please try again.");
    }
  };

  return (
    <div className="add-event-form">
      <h1>Add New Event</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="event_name"
          placeholder="Event Name"
          value={eventData.event_name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="country"
          placeholder="Country"
          value={eventData.country}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="state"
          placeholder="State"
          value={eventData.state}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="city"
          placeholder="City"
          value={eventData.city}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={eventData.address}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="category"
          placeholder="Category"
          value={eventData.category}
          onChange={handleChange}
        />
        <input
          type="datetime-local"
          name="dateandtime"
          placeholder="Date and Time"
          value={eventData.dateandtime}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="image_url"
          placeholder="Image URL"
          value={eventData.image_url}
          onChange={handleChange}
          required
        />
        {/* New organised_by Input */}
        <input
          type="text"
          name="organised_by"
          placeholder="Organised By"
          value={eventData.organised_by}
          onChange={handleChange}
          required
        />
        {/* New description Input */}
        <textarea
          name="description"
          placeholder="Description"
          value={eventData.description}
          onChange={handleChange}
          required
          rows="4" // Adjust for description length
          className="description-input" // Optional class for styling
        />
        <button type="submit" className="submit-btn">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddEvent;
