import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "./eventdetails.css";

const EventDetails = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  // Simulated role (In actual implementation, retrieve it from context, JWT, or localStorage)
  const userRole = localStorage.getItem("userRole") || "user"; // "admin" or "user"

  const [isBooked, setIsBooked] = useState(event?.isBooked || false);

  useEffect(() => {
    document.body.classList.add("background");
    return () => {
      document.body.classList.remove("background");
    };
  }, []);

  if (!event) {
    return <p>No event details available.</p>;
  }

  // POST API to Book Event
  const handleBookEvent = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/bookevent", {
        event_id: event.event_id,
        user_id: localStorage.getItem("userId"), // Simulated user ID
      });

      if (response.status === 200) {
        setIsBooked(true);
        alert("Event successfully booked!");
      }
    } catch (error) {
      console.error("Error booking event:", error);
      alert("Failed to book the event. Please try again.");
    }
  };

  return (
    <div className="event-details">
      <button className="go-back-btn" onClick={() => navigate("/getevents")}>
        &#8592; Back to Events
      </button>
      <h1>{event.event_name}</h1>
      <img
        src={`http://127.0.0.1:8000/${event.image_url}`}
        alt={event.event_name}
        className="event-details-image"
      />
      <p className="description">
        <strong>Description:</strong> {event.description_event}
      </p>
      <p>
        <strong>Date and Time:</strong>{" "}
        {new Date(event.dateandtime).toLocaleString()}
      </p>
      <p>
        <strong>Category:</strong> {event.category}
      </p>
      <p>
        <strong>Organised By:</strong> {event.organised_by}
      </p>
      <p>
        <strong>Location:</strong>{" "}
        {`${event.city}, ${event.state}, ${event.country}`}
      </p>
      <p>
        <strong>Address:</strong> {event.address}
      </p>

      {userRole === "user" && !isBooked && (
        <button className="book-btn" onClick={handleBookEvent}>
          Book Event
        </button>
      )}

      {isBooked && <p className="booked-notice">Event Already Booked!</p>}
    </div>
  );
};

export default EventDetails;
